INSERT INTO `test_question` VALUES ('4ec4ed3bc28b4e9ab8ff61eb73f57200', '公安机关在侦办钱某诈骗案时，对钱某的住处进行了搜查，并对搜查过程中所获取的有关物品进行了扣押下列说法正确的是（）', 'A．搜查时发现的涉案物品，办案人员应当进行扣押;B．进行扣押时，应制作扣押清单;C．持有人拒绝交出应当扣押的物品的，公安机关不得强制扣押，应当予以登记;D．公安机关在侦查过程中，如果发现其中被扣押的某些物品与本案无关时，应当在3日以内返还物品持有人&nbsp;', 'ABD', '2', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
