INSERT INTO `test_question` VALUES ('4f9e4eecb87346b38283db7d55238a08', '甲县公安局民警王某对涉嫌盗窃罪的犯罪嫌疑人李某采用殴打的方式获取口供，最终李某因证据不足被释放李某多次上访要求对刑讯逼供的王某给予处罚，甲县公安局对王某停止执行职务15日在停职期间，王某为了教训李某，以涉嫌盗窃罪为名，将李某带至一宾馆内，限制李某人身自由达10日之久最终李某无法忍受，跳窗逃脱时不慎摔死王某非法拘禁一案由甲县公安机关办理，而王某刑讯逼供一案移交甲县人民检察院办理', null, 'Yes', '3', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
