INSERT INTO `test_question` VALUES ('5f4f1c4d334e40d6b22162af0bf0c236', '刘某为获取财物，于某日夜间在乡间小路持刀对路过的下班工人实施抢劫，致使被害人死亡，抢得现金500元，公安机关根据线索将刘某抓获下列关于讯问刘某的说法，违反法律规定的是()', 'A．应当根据刘某交代案情的情况决定讯问时是否对刘某进行录音或录像;B．对讯问刘某进行录音或录像应当全程进行;C．讯问笔录应当由刘某签名或盖章;D．因案情重大，公安机关可以安排3名民警对刘某进行讯问&nbsp;', 'A', '1', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
