INSERT INTO `test_question` VALUES ('5f834b7e87274cc79f300187ce26b0b7', '(17年新增试题)某市公安局在侦办电信网络诈骗犯罪案件时，发现涉案电子数据量大，无法提取针对此情形,该市公安局办案部门负责人可以批准冻结涉案的电子数据', null, 'No', '3', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
