INSERT INTO `test_question` VALUES ('890bea6d03384082b9c3dec6e69510f6', '某公安局在侦查国家机关工作人员韩某强奸邓某案件时，发现其巨额财产来源不明，下列说法正确的是（）', 'A.由公安机关分别立案侦查;B.将案件并案后移送人民检察院，以人民检察院为主进行侦查，公安机关予以配合;C.将案件并案后以公安机关为主进行侦查，人民检察院予以配合;D.将巨额财产来源不明案件移送人民检察院&nbsp;', 'D', '1', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
