INSERT INTO `test_question` VALUES ('8b17d70f166341f1a239794f81f3c8c3', '犯罪嫌疑人张某某，因涉嫌故意伤害罪被公安机关立案侦查在侦查阶段，犯罪嫌疑人张某某可以行使的诉讼权利有（　）', 'A．聘请律师作为辩护人;B．会见聘请的律师;C．委托聘请的律师申请取保候审，代理申诉、控告;D．要求律师复印侦查案卷&nbsp;', 'ABC', '2', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
