INSERT INTO `test_question` VALUES ('9955aff5828048b88e9141cea43f3e48', '甲因涉嫌抢夺罪被人民检察院批准逮捕，公安机关执行逮捕后，下列说法中正确的是（）', 'A．应当在24小时以内通知甲的家属，有碍侦查和无法通知的除外;B．应当在24小时以内对甲进行讯问，发现不应当逮捕的，必须立即释放;C．如对甲变更为取保候审，应当通知原批准的人民检察院;D．人民检察院应当对甲羁押的必要性继续进行审查&nbsp;', 'BCD', '2', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
