INSERT INTO `test_question` VALUES ('1d7d17c64b56479b9a84dd7a08305fef', '(17年新增试题)下列关于虚假诉讼罪的说法正确的是（）。', 'A．以捏造的事实提起民事诉讼，妨害司法秩序或者严重侵害他人合法权益的行为构成虚假诉讼罪;B．在刑事诉讼中，辩护人、诉讼代理人伪造证据的行为构成虚假诉讼罪;C．自然人和单位均可以作为虚假诉讼罪的犯罪主体;D．司法工作人员利用职权，以捏造的事实提起民事诉讼，妨害司法秩序或者严重侵害他人合法权益，同时构成渎职罪的，应当以虚假诉讼罪和渎职罪数罪并罚&nbsp;', 'AC', '2', '84095a32f42d4bde8306ed14ffd73ab7', null, null, '2018-06-23 00:48:24', '2018-06-23 00:48:24', '1');
