INSERT INTO `test_question` VALUES ('38a3950c3873433fb98b20353f327d57', '某市公安局出入境管理部门的民警在执法过程中发现涉嫌违反出境入境管理的人员，经当场盘问，具有下列（）情形的，可以依法继续盘问。', 'A．有非法出境入境嫌疑的;B．有协助他人非法出境入境嫌疑的;C．外国人有非法居留嫌疑的;D．外国人有非法就业嫌疑的&nbsp;', 'ABCD', '2', '4f75bf2e7f0548969c0989a7416b4cd7', null, null, '2018-06-23 00:55:02', '2018-06-23 00:55:02', '1');
