INSERT INTO `test_question` VALUES ('3903db1fa36341999ca1386a29262c2a', '某县公安局在侦查李某集资诈骗案中，李某死亡，有证据证明李某在银行有存款，该存款属于违法所得应当予以没收下列说法错误的是（）', 'A.可以进行调查;B.可以对该存款进行冻结;C.没收违法所得意见书应当经该县公安局局长批准;D.该县公安局应当向人民法院移送没收违法所得意见书&nbsp;', 'D', '1', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
