INSERT INTO `test_question` VALUES ('39ec58b88f2b475f8598ae8f0973023e', '甲出于报复目的，在公共场所对一些无辜群众注射了生理盐水，谎称是艾滋病病毒，引起被害人和周围群众极度恐慌，严重扰乱社会秩序。甲的行为（）。', 'A．不构成犯罪;B．构成投放虚假危险物质罪;C．构成故意伤害罪;D．构成以危险方法危害公共安全罪&nbsp;', 'B', '1', '84095a32f42d4bde8306ed14ffd73ab7', null, null, '2018-06-23 00:48:24', '2018-06-23 00:48:24', '1');
