INSERT INTO `test_question` VALUES ('3a46155963634f2e9a180d0c3bb3d389', '下列刑事案件中，不应当由公安机关立案侦查的是（）', 'A．某县县长贿赂选民案件;B．某县富商非法拘禁长期欠其钱款不还的朱某，要求其家人限期还钱;C．某侦查人员在一刑事案件中对犯罪嫌疑人刑讯逼供致人重伤;D．某局长指使别人对举报其索贿受贿的下属郭某报复陷害&nbsp;', 'ACD', '2', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
