INSERT INTO `test_question` VALUES ('5159eb3fb88443b09cebaae1e4240f1b', '(17年新增试题)章某涉嫌领导恐怖组织罪被甲市（设区的市）公安机关监视居住，在其自己的住处执行，现发现章某有毁灭证据的迹象下列说法正确的是（）', 'A．因章某存在有碍侦查的情形，甲市公安机关可以直接决定对其指定居所执行;B．对章某指定居所监视居住执行后24小时以内应当通知家属;C．对章某监视居住的期限应当折抵刑期;D．如对章某需要予以逮捕，可以对其先行拘留&nbsp;', 'BD', '2', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
