INSERT INTO `test_question` VALUES ('52db4dfbc2cb4becb3e0771f9b0bdd7f', '公安机关在办理一起聚众斗殴案件过程中，发现现场处于一家单位安装的监控设备覆盖范围内，并了解到该单位的监控设备运行正常，侦查人员决定到该单位调取视频资料下列说法错误的是（）', 'A．应当告知该单位必须如实提供证据;B．调取证据应当经办案部门负责人批准;C．应当开具调取证据通知书;D．采用录音或者录像等方式固定取证过程，可不制作提取笔录和清单&nbsp;', 'D', '1', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
