INSERT INTO `test_question` VALUES ('6fb7a27eb9094badb052dd51c0ec2d4c', '下列关于未成年人犯罪的说法中正确的是（）。', 'A．甲，15周岁，在组织他人偷越国（边）境过程中，使用暴力造成一人重伤。甲不构成犯罪;B．乙，15周岁，与13周岁的低年级女生A谈恋爱，两人多次自愿发生性关系，A因怀孕中途退学。乙构成强奸罪;C．丙，15周岁，在盗窃过程中被发现，为抗拒抓捕，当场使用暴力，致使被害人死亡。丙构成抢劫罪;D．丁，17周岁，恃强凌弱，多次强行索要其他未成年人随身携带的钱财、学习用品，对于没带钱的同学动辄打骂威胁，致使多名学生不敢正常到校学习，扰乱学校秩序，情节严重。丁构成寻衅滋事罪&nbsp;', 'BD', '2', '32ab44d9ba4649d8957ba3680cadb928', null, null, '2018-06-23 00:51:35', '2018-06-23 00:51:35', '1');
