INSERT INTO `test_question` VALUES ('782d28e6bdf14ee3904ffc2bbe554828', '对于下列案件定性处理正确的是（）。', 'A．甲在大街上看见一妇女张某独自行走，尾随一段路后，拽走张某的挎包，张某追赶甲，甲回头把张某踹倒在地后逃离，经鉴定，张某受轻微伤。甲构成抢夺罪;B．乙因抢夺一路人被当场抓获，在他身上搜出一把匕首。乙构成抢劫罪;C．丙伙同他人骑一辆摩托车在巷子里把妇女刘某逼挤到墙角后，把挎包抢走。丙构成抢夺罪;D．丁看见前路上一个小孩（4岁）独自玩耍，上前猛力拽下其脖子上的银项链，孩子被吓得哇哇大哭。丁构成抢劫罪&nbsp;', 'BD', '2', '84095a32f42d4bde8306ed14ffd73ab7', null, null, '2018-06-23 00:48:24', '2018-06-23 00:48:24', '1');
