INSERT INTO `test_question` VALUES ('7bc662b49f2f42758b08fba8c3fc96c6', '下列情形中，对有证据证明有犯罪事实，可能判处徒刑以上刑罚的犯罪嫌疑人、被告人，采取取保候审尚不足以防止发生社会危险性，应当予以逮捕的是（）', 'A．甲有危害国家安全、公共安全或者社会秩序的现实危险的;B．乙可能毁灭、伪造证据，干扰证人作证或者串供的;C．丙可能对被害人实施打击报复的;D．丁企图自杀&nbsp;', 'ABCD', '2', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
