INSERT INTO `test_question` VALUES ('7cd81fd578144866bcaf97b8ebb3cfbf', '乙（16周岁）进城打工，用人单位要求乙提供银行卡号以便发放工资。乙忘记携带身份证，便借用老乡甲的身份证以甲的名义办理了银行卡。乙将银行卡号提供给用人单位后，请甲保管银行卡。数月后，甲持该卡到银行柜台办理密码挂失，取出1万余元现金，拒不退还。甲的行为构成（　）。', 'A．信用卡诈骗罪;B．诈骗罪;C．盗窃罪;D．侵占罪&nbsp;', 'D', '1', '32ab44d9ba4649d8957ba3680cadb928', null, null, '2018-06-23 00:51:35', '2018-06-23 00:51:35', '1');
