INSERT INTO `test_question` VALUES ('82201f4cbefc400ebd3102ae0520ea66', '下列关于侦查羁押期限的说法正确的是（）', 'A．犯罪嫌疑人不讲真实姓名、住址的，侦查羁押期限自其供述真实姓名、住址之日起计算，但不得停止对其犯罪行为的侦查取证;B．犯罪嫌疑人不讲真实姓名、住址的，如果犯罪事实清楚，证据确实、充分的，不能按其自报的姓名移送人民检察院审查起诉;C．犯罪嫌疑人可能判处有期徒刑以上刑罚，延长期限届满，仍不能侦查终结的，经省、自治区、直辖市人民检察院批准，可以再延长2个月;D．在侦查期间，发现犯罪嫌疑人另有重要罪行的，应当自发现之日起5日内报县级以上公安机关负责人批准后，重新计算侦查羁押期限&nbsp;', 'D', '1', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
