INSERT INTO `test_question` VALUES ('9e783d7695534fa8bfa6f9cae4860e52', '李树是越南人，因非法居留被公安机关发现，后被拘留审查。她认为自己已怀孕3个月，中国警方拘留审查违法，便根据《出境入境管理法》的规定申请行政复议。如其对行政复议决定不服，还可以提起行政诉讼。&nbsp;', null, 'No', '3', 'a508cc7c75514042801f42cfd3176592', null, null, '2018-05-08 22:28:49', '2018-05-08 22:28:49', '1');
