INSERT INTO `test_question` VALUES ('b8c3f347e7944b57a8440034fcc51172', '下列行为中，不构成帮助恐怖活动罪的是（）。', 'A．甲给予恐怖活动组织“红心会”10万美元;B．乙无偿给予恐怖分子张某价值30万元的汽车一辆;C．丙应邀参加恐怖活动，负责制造10枚炸弹，其还在火车站实施爆炸2起，造成3名无辜群众死亡;D．丁积极为某恐怖活动组织招兵买马&nbsp;', 'C', '1', '84095a32f42d4bde8306ed14ffd73ab7', null, null, '2018-06-23 00:48:24', '2018-06-23 00:48:24', '1');
