INSERT INTO `test_question` VALUES ('ba2e683c4ea8443a88d937d41affe4ba', '下列有关刑罚的说法正确的是（）。', 'A．甲（17周岁）因故意伤害罪被判处4年有期徒刑，若甲符合缓刑的条件，则对甲可以适用缓刑;B．乙（76周岁）犯失火罪，对乙可以减轻或者免除处罚;C．丙犯贪污罪，宣判当日是其75周岁生日，可以根据其犯罪情节对其适用死刑;D．丁（78周岁）长年虐待自己的女儿，并用针头、铁钳等工具对其施加暴力，造成其重伤，丁的残忍手段引起公愤，对丁可以适用死刑&nbsp;', 'C', '1', '32ab44d9ba4649d8957ba3680cadb928', null, null, '2018-06-23 00:51:35', '2018-06-23 00:51:35', '1');
