INSERT INTO `test_question` VALUES ('c70ce555ac0d415485f1c541f9aae709', '(17年新增试题)苏某涉嫌参加恐怖组织罪被执行逮捕苏某同事翁某为知情人员，公安机关尚未对翁某取证同案犯柳某在逃下列关于通知苏某家属的说法正确的是（）', 'A．应当对证人翁某询问结束后再通知家属;B．应当在同案犯柳某被抓获后再通知家属;C．应当在对苏某执行逮捕后24小时内通知家属;D．若未在24小时内通知家属的，应当在讯问笔录中注明&nbsp;', 'C', '1', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
