INSERT INTO `test_question` VALUES ('ca9a97919a52422faee0acdf022b5c58', '某县、乡两级人大代表王某因涉嫌犯罪被通缉下列说法正确的是（）', 'A．经过批准，可以采取追捕所必需的技术侦查措施;B．提请逮捕前，应当报请王某所属的乡人民代表大会许可;C．提请逮捕前，应当报请王某所属的县级人民代表大会主席团或者常务委员会许可;D．王某被抓获后，可以提取其指纹信息，采集血液、尿液等生物样本&nbsp;', 'ACD', '2', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
