INSERT INTO `test_question` VALUES ('ce2d046a282b40a98a4208d6f4928823', '甲专门制造假证件，受乙委托，伪造了5本机动车行驶证，乙将5本机动车行驶证分别卖给了不同的买家，案发后，甲、乙二人到案。对甲、乙二人行为定性准确的是（）。', 'A.对甲的行为以伪造国家机关证件罪定罪处罚;B.对乙的行为以买卖国家机关证件罪定罪处罚;C.对甲的行为以买卖国家机关证件罪的共犯定罪处罚;D.对乙的行为以掩饰、隐瞒犯罪所得罪定罪处罚&nbsp;', 'AB', '2', '84095a32f42d4bde8306ed14ffd73ab7', null, null, '2018-06-23 00:48:24', '2018-06-23 00:48:24', '1');
