INSERT INTO `test_question` VALUES ('cf1c54b1966e489b8dda0d8e1c722e67', '下列行为，依法应当适用我国《刑法》的是（）。', 'A．美国公民甲在美国境内准备了作案工具，潜入我国境内准备杀害日本商人乙，在跟踪乙时被抓获;B．在我国登记注册、悬挂我国国旗的某轮船航行到某公海领域时，船上H国公民丙杀死了F国公民丁;C．我国在新西兰的留学生周某将英国学生乙打成重伤;D．我国一架国际航班进入俄罗斯境内时，乘坐该航班的美国人甲因争执将法国人乙打死&nbsp;', 'ABCD', '2', '84095a32f42d4bde8306ed14ffd73ab7', null, null, '2018-06-23 00:48:24', '2018-06-23 00:48:24', '1');
