INSERT INTO `test_question` VALUES ('d59c48c24a614788827d2d4c33b51a84', '(17年新增试题)甲为寻求刺激，请求乙帮助他下载一些淫秽视频供自己观看。乙帮助甲在网上下载500余部淫秽视频录像，并以甲的名义申请了一个网络云盘，将下载的淫秽视频录像全部存入云盘内。乙的行为（）。', 'A．构成传播淫秽物品罪;B．构成非法利用信息网络罪;C．构成帮助信息网络犯罪活动罪;D．不构成犯罪&nbsp;', 'D', '1', '32ab44d9ba4649d8957ba3680cadb928', null, null, '2018-06-23 00:51:35', '2018-06-23 00:51:35', '1');
