INSERT INTO `test_question` VALUES ('e1892bf7dc51443088fae7b788fdc38b', '甲见一高级轿车内有一个公文包，便砸碎车窗玻璃将包拿走。包中有5000元现金、一支手枪、三份国家机关的重要公文和一个工作证，甲将现金和枪留下，将公文和证件烧掉。以下说法正确的是（）。', 'A．对甲应择一重罪处罚;B．甲构成盗窃罪，非法持有枪支罪，毁灭国家机关公文、证件罪;C．甲构成盗窃罪，非法持有枪支罪，掩饰、隐瞒犯罪所得罪;D．对甲应数罪并罚&nbsp;', 'BD', '2', '84095a32f42d4bde8306ed14ffd73ab7', null, null, '2018-06-23 00:48:24', '2018-06-23 00:48:24', '1');
