INSERT INTO `test_question` VALUES ('ecd5a08fbe9a41ad89f907cdbabf709f', '某机械师对领导不满，利用工作之便，在进厂大修的飞机上装了两套控制电路，意图在飞机试飞时断油停机，造成机毁人亡的严重事故。后被及时发现，未造成预期后果。该机械师的行为（）。', 'A．构成破坏交通工具罪（既遂）;B．构成破坏交通工具罪（未遂）;C．构成破坏交通工具罪（预备）;D．不构成犯罪&nbsp;', 'A', '1', '32ab44d9ba4649d8957ba3680cadb928', null, null, '2018-06-23 00:51:35', '2018-06-23 00:51:35', '1');
