INSERT INTO `test_question` VALUES ('f9ba121b68e746178a67a8c417c3fe50', '王某系某公安派出所户籍民警，受朋友委托，采取伪造公民地址，编写迁入地区号段的身份证编号等手段，为不符合落户条件的8名外市户籍人员办理了本市户籍。王某构成（）。', 'A.徇私枉法罪;B.玩忽职守罪;C.滥用职权罪;D.伪造国家机关公文、证件罪&nbsp;', 'C', '1', '32ab44d9ba4649d8957ba3680cadb928', null, null, '2018-06-23 00:51:35', '2018-06-23 00:51:35', '1');
