INSERT INTO `test_question` VALUES ('fa08d51c385741028ce80406aa3cfbfe', '犯罪嫌疑人李某因持械抢劫被甲县公安机关刑事拘留，在公安机关侦查期间，（　）', 'A．李某有权委托律师作为其辩护人;B．李某有权委托律师为其提供法律帮助，但无权委托辩护人;C．李某委托的律师有权与其会见，但须经公安机关许可;D．李某委托的律师有权与其会见，无须经公安机关许可&nbsp;', 'AD', '2', 'cf625c6029fe4d13858866e8a7484148', null, null, '2018-05-07 16:31:31', '2018-05-07 16:31:31', '1');
