INSERT INTO `test_question` VALUES ('fba25f3828874a7ebaf2f21e76f22edb', '下列情形中，应当受追诉时效限制的是（）。', 'A．在人民法院受理了齐某自诉汤某伤害案件以后，汤某离家杳无音信;B．丛某因故未在法定期限内对钟某的侮辱行为提出控告;C．薛某向公安机关控告，声称自己被抢劫，因薛某说话颠三倒四，接案人员对其报案有怀疑而未立案;D．秦某得知与其共同诈骗的李某被公安机关抓获，于是逃离居住地藏匿&nbsp;', 'B', '1', '84095a32f42d4bde8306ed14ffd73ab7', null, null, '2018-06-23 00:48:24', '2018-06-23 00:48:24', '1');
